// Vertex Class.

public class Vertex {

	int id;
	double xLabel;
	double yLabel;
	double radius;
	boolean visited;

	public Vertex(int id, double xLabel, double yLabel, double radius) { // Constructor.
		// TODO Auto-generated constructor stub
		this.id = id;
		this.xLabel = xLabel;
		this.yLabel = yLabel;
		this.radius = radius;
	}

}
